    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:

        String id = jTextField1.getText();

        //To PRINT
        /*System.out.println(name);
        System.out.println(dob);
        System.out.println(gender);
        System.out.println(address);
        System.out.println(course);
        System.out.println(doj);*/
        try {
            Class.forName("oracle.jdbc.OracleDriver");//to load the DRIVER
            Connection con = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:orcl", "system", "pass1234");
//to establish connection between java and sql

            PreparedStatement ps = con.prepareStatement("select * from students where id=?");

            ps.setString(1, id);//since we are fetching only string values, we are using the function setString otherwise setInt if we are fetching integer values.
            /* ps.setString(2,gender);
            ps.setString(3,dob);
            ps.setString(4,course);
            ps.setString(5,address);
            ps.setString(6, doj);*/

            ResultSet rs = ps.executeQuery();
            while (rs.next()) {
                tf2.setText(rs.getString(2));
                tf3.setText(rs.getString(3));
                tf4.setText(rs.getString(4));
                tf5.setText(rs.getString(5));
                tf6.setText(rs.getString(6));
                tf7.setText(rs.getString(7));

                System.out.println("ID: "+rs.getString(1));
            System.out.println("Name: "+rs.getString(2));
                
            System.out.println("DOB: "+rs.getString(3));
            System.out.println("Address: "+rs.getString(4));
            System.out.println("Gender: "+rs.getString(5));
            System.out.println("Course: "+rs.getString(6));
            System.out.println("DOJ: "+rs.getString(7));
            }

            JOptionPane.showConfirmDialog(jButton1, "Viewed Successfully");
        }
        catch (Exception e) {
            System.out.println(e);
        }


    }//GEN-LAST:event_jButton1ActionPerformed

