    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        // TODO add your handling code here:
        String name = tf2.getText();
        String dob = tf3.getText();
        String address = tf4.getText();
        String gender = tf5.getText();
        String course = tf6.getText();
        String doj = tf7.getText();
        String id=jTextField1.getText();

        try {
            Class.forName("oracle.jdbc.OracleDriver");

            Connection con = DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:orcl", "system", "pass1234");

            PreparedStatement ps = con.prepareStatement("update students set name=?,dob=?,address=?,gender=?,course=?,doj=? where id=?");

            ps.setString(1, name);
            ps.setString(2, dob);
            ps.setString(3, address);
            ps.setString(4, gender);
            ps.setString(5, course);
            ps.setString(6, doj);
            ps.setString(7,id);
            ResultSet rs = ps.executeQuery();


    }//GEN-LAST:event_jButton3ActionPerformed
catch (Exception e) {
            System.out.println(e);
        }
    }
